mapper.select:
SELECT *
FROM task
WHERE userId = #{userId} AND projectId = #{projectId};

SELECT COUNT(*) FROM task
WHERE STR_TO_DATE(enddate, '%Y-%m-%d') < #{now}
	AND userId=#{userId}
	AND projectId=#{projectId}
	AND progress!=100;

SELECT COUNT(*) FROM task
WHERE STR_TO_DATE(startdate, '%Y-%m-%d') < #{now}
AND STR_TO_DATE(enddate, '%Y-%m-%d') > #{now}
AND userId = #{userId}
AND projectId = #{projectId}
AND status = "진행중"
AND progress != 100;

mapper.update:
UPDATE task
SET category=#{category}, workPackage=#{workPackage}, depth=#{depth}, detail=#{detail}, userId=#{userId},
startdate=#{startdate}, enddate=#{enddate}, status=#{status}
WHERE projectId=#{projectId} AND taskId=#{taskId}
